def printrw(*args, **kwargs):
    '''fancy reproduce.work print function'''
    # if the first arg is a string, prepend it with ‚ïîœâ
    if len(args) &gt; 0 and isinstance(args[0], str):
        args = ("‚ïîœâ: "+args[0], *args[1:])

    # for each arg, replace newlines with ‚ïö
    if len(args) &gt; 0:
        new_args = []
        for a in args[:-1]:
            if isinstance(a, str):  
                a = a.replace("\n", "\n‚ïë ")

            new_args.append(a)
        if isinstance(args[:-1], str): 
            new_args.append(args[:-1].replace("\n", "\n‚ïö "))
        else:
            new_args.append(args[-1])
        args = tuple(new_args)

    print(*args, **kwargs, flush=True)



